{"metadata":{"kernelspec":{"language":"python","display_name":"Python 3","name":"python3"},"language_info":{"pygments_lexer":"ipython3","nbconvert_exporter":"python","version":"3.6.4","file_extension":".py","codemirror_mode":{"name":"ipython","version":3},"name":"python","mimetype":"text/x-python"},"kaggle":{"accelerator":"none","dataSources":[],"dockerImageVersionId":31192,"isInternetEnabled":true,"language":"python","sourceType":"notebook","isGpuEnabled":false}},"nbformat_minor":4,"nbformat":4,"cells":[{"source":"<a href=\"https://www.kaggle.com/code/abhijitbiswas21/agent-assist1?scriptVersionId=280045488\" target=\"_blank\"><img align=\"left\" alt=\"Kaggle\" title=\"Open in Kaggle\" src=\"https://kaggle.com/static/images/open-in-kaggle.svg\"></a>","metadata":{},"cell_type":"markdown"},{"cell_type":"code","source":"!pip install google-adk nltk google-generativeai\nprint(\"‚úÖ All libraries installed successfully!\")","metadata":{"_uuid":"a567696f-9567-4b52-b689-26ace9449319","_cell_guid":"e50a3856-e14b-4d49-a8ef-4af3c6444fab","trusted":true,"collapsed":false,"jupyter":{"outputs_hidden":false}},"outputs":[],"execution_count":null},{"cell_type":"code","source":"import os\nfrom kaggle_secrets import UserSecretsClient\nimport nltk\nfrom nltk.tokenize import sent_tokenize\nfrom google.adk.tools import AgentTool, ToolContext, load_memory\nfrom google.adk.models.google_llm import Gemini\nfrom google.genai import types\nfrom google.adk.agents import Agent, SequentialAgent\nfrom google.adk.runners import InMemoryRunner\nfrom google.adk.tools import google_search\n\nprint(\"‚úÖ ADK components imported successfully.\")","metadata":{"trusted":true},"outputs":[],"execution_count":null},{"cell_type":"code","source":"try:\n    GOOGLE_API_KEY = UserSecretsClient().get_secret(\"GOOGLE_API_KEY\")\n    os.environ[\"GOOGLE_API_KEY\"] = GOOGLE_API_KEY\n    print(\"‚úÖ Gemini API key setup complete.\")\nexcept Exception as e:\n    print(\n        f\"üîë Authentication Error: Please make sure you have added 'GOOGLE_API_KEY' to your Kaggle secrets. Details: {e}\"\n    )","metadata":{"trusted":true},"outputs":[],"execution_count":null},{"cell_type":"code","source":"from IPython.core.display import display, HTML\nfrom jupyter_server.serverapp import list_running_servers\ndef get_adk_proxy_url():\n    PROXY_HOST = \"https://kkb-production.jupyter-proxy.kaggle.net\"\n    ADK_PORT = \"8000\"\n\n    servers = list(list_running_servers())\n    if not servers:\n        raise Exception(\"No running Jupyter servers found.\")\n\n    baseURL = servers[0][\"base_url\"]\n\n    try:\n        path_parts = baseURL.split(\"/\")\n        kernel = path_parts[2]\n        token = path_parts[3]\n    except IndexError:\n        raise Exception(f\"Could not parse kernel/token from base URL: {baseURL}\")\n\n    url_prefix = f\"/k/{kernel}/{token}/proxy/proxy/{ADK_PORT}\"\n    url = f\"{PROXY_HOST}{url_prefix}\"\n\n    styled_html = f\"\"\"\n    <div style=\"padding: 15px; border: 2px solid #f0ad4e; border-radius: 8px; background-color: #fef9f0; margin: 20px 0;\">\n        <div style=\"font-family: sans-serif; margin-bottom: 12px; color: #333; font-size: 1.1em;\">\n            <strong>‚ö†Ô∏è IMPORTANT: Action Required</strong>\n        </div>\n        <div style=\"font-family: sans-serif; margin-bottom: 15px; color: #333; line-height: 1.5;\">\n            The ADK web UI is <strong>not running yet</strong>. You must start it in the next cell.\n            <ol style=\"margin-top: 10px; padding-left: 20px;\">\n                <li style=\"margin-bottom: 5px;\"><strong>Run the next cell</strong> (the one with <code>!adk web ...</code>) to start the ADK web UI.</li>\n                <li style=\"margin-bottom: 5px;\">Wait for that cell to show it is \"Running\" (it will not \"complete\").</li>\n                <li>Once it's running, <strong>return to this button</strong> and click it to open the UI.</li>\n            </ol>\n            <em style=\"font-size: 0.9em; color: #555;\">(If you click the button before running the next cell, you will get a 500 error.)</em>\n        </div>\n        <a href='{url}' target='_blank' style=\"\n            display: inline-block; background-color: #1a73e8; color: white; padding: 10px 20px;\n            text-decoration: none; border-radius: 25px; font-family: sans-serif; font-weight: 500;\n            box-shadow: 0 2px 5px rgba(0,0,0,0.2); transition: all 0.2s ease;\">\n            Open ADK Web UI (after running cell below) ‚Üó\n        </a>\n    </div>\n    \"\"\"\n\n    display(HTML(styled_html))\n\n    return url_prefix\n\n\nprint(\"‚úÖ Helper functions defined.\")","metadata":{"trusted":true},"outputs":[],"execution_count":null},{"cell_type":"code","source":"retry_config = types.HttpRetryOptions(\n    attempts=5,  # Maximum retry attempts\n    exp_base=7,  # Delay multiplier\n    initial_delay=1,  # Initial delay before first retry (in seconds)\n    http_status_codes=[429, 500, 503, 504]  # Retry on these HTTP errors\n)\n\n\nasync def auto_save_to_memory(callback_context):\n    \"\"\"Automatically persists session data to long-term memory.\"\"\"\n    if hasattr(callback_context._invocation_context, 'memory_service'):\n        await callback_context._invocation_context.memory_service.add_session_to_memory(\n            callback_context._invocation_context.session\n        )\n\n\n# Tool to save user preferences into Session State\ndef set_user_context(tool_context: ToolContext, role: str, focus_area: str):\n    \"\"\"\n    Saves the user's role and focus area to the current session state.\n    Use this when the user introduces themselves (e.g., \"I am a Product Manager\").\n    \"\"\"\n    tool_context.state[\"user:role\"] = role\n    tool_context.state[\"user:focus\"] = focus_area\n    return f\"Context saved: Role={role}, Focus={focus_area}\"\n\n\nprint(\"‚úÖ Retry config and callbacks defined.\")\n","metadata":{"trusted":true},"outputs":[],"execution_count":null},{"cell_type":"code","source":"# --- FALLBACK TOOLS ---\ndef offline_meeting_fallback(text: str):\n    sentences = sent_tokenize(text)\n    actions = [s for s in sentences if any(w in s.lower() for w in ['todo', 'action', 'deadline'])]\n    return f\"[OFFLINE FALLBACK] Actions detected: {len(actions)}\"\n\n\ndef offline_research_fallback(text: str):\n    sentences = sent_tokenize(text)\n    tldr = sentences[0] if sentences else \"No text\"\n    return f\"[OFFLINE FALLBACK] TL;DR: {tldr}\"\n\n\nprint(\"‚úÖ Fallback tools defined.\")\n","metadata":{"trusted":true},"outputs":[],"execution_count":null},{"cell_type":"code","source":"model = Gemini(model=\"gemini-2.5-flash-lite\", retry_options=retry_config)\n\nprint(\"‚úÖ Model initialized.\")","metadata":{"trusted":true},"outputs":[],"execution_count":null},{"cell_type":"code","source":"# [A] Meeting Agent\nmeeting_agent = Agent(\n    name=\"MeetingPrepAgent\",\n    model=model,\n    instruction=\"Analyze the transcript. Output: Pre-Brief, Agenda, and Action Items.\",\n    tools=[offline_meeting_fallback]\n)\n\nprint(\"‚úÖ Meeting Agent defined.\")","metadata":{"trusted":true},"outputs":[],"execution_count":null},{"cell_type":"code","source":"# [B] Research Pipeline\nextractor = Agent(\n    name=\"FactExtractor\",\n    model=model,\n    instruction=\"Extract Top 5 Key Findings and Sources from the provided content.\",\n    output_key=\"facts\"\n)\n\nwriter = Agent(\n    name=\"BriefWriter\",\n    model=model,\n    instruction=\"Using the previous agent's extracted findings, write a Decision-Ready Brief with TL;DR, Findings, and Next Steps.\",\n)\n\nresearch_pipeline = SequentialAgent(\n    name=\"ResearchSummaryAgent\",\n    sub_agents=[extractor, writer]\n)\n\nprint(\"‚úÖ Research Pipeline defined.\")\n","metadata":{"trusted":true},"outputs":[],"execution_count":null},{"cell_type":"code","source":"# [C] Orchestrator (Router with Memory)\nroot_agent = Agent(\n    name=\"AgentAssist_Orchestrator\",\n    model=model,\n    instruction=\"\"\"You are the AgentAssist Orchestrator.\n    \n    1. CHECK CONTEXT: Use 'load_memory' to see if you know the user's preferences.\n    2. SAVE CONTEXT: If the user states their role/job, use 'set_user_context'.\n    3. ROUTE:\n       - Meeting notes -> 'MeetingPrepAgent'\n       - Documents -> 'ResearchSummaryAgent'\n       - General -> Answer directly using Google Search if needed\n    \"\"\",\n    tools=[AgentTool(meeting_agent), AgentTool(research_pipeline), google_search, load_memory, set_user_context],\n    after_agent_callback=auto_save_to_memory  # Enables auto-memory storage\n)\nprint(\"‚úÖ Root Agent defined.\")\nroot_agent = Agent(\n    name=\"helpful_assistant\",\n    model=Gemini(\n        model=\"gemini-2.5-flash\",\n        retry_options=retry_config\n    ),\n    description=\"A simple agent that can answer general questions.\",\n    instruction=\"You are a helpful assistant. Use Google Search for current info or if unsure.\",\n    tools=[google_search],\n)\nprint(\"‚úÖ Root Agent defined.\")\n","metadata":{"trusted":true},"outputs":[],"execution_count":null},{"cell_type":"code","source":"runner = InMemoryRunner(agent=root_agent)\n\nprint(\"‚úÖ Runner created.\")","metadata":{"trusted":true},"outputs":[],"execution_count":null},{"cell_type":"code","source":"# Test query\nresponse = await runner.run_debug(\n    \"What is Agent Development Kit from Google? What languages is the SDK available in?\"\n)","metadata":{"trusted":true},"outputs":[],"execution_count":null},{"cell_type":"code","source":"!adk create Ai-agent --model gemini-2.5-flash-lite --api_key $GOOGLE_API_KEY","metadata":{"trusted":true},"outputs":[],"execution_count":null},{"cell_type":"code","source":"url_prefix = get_adk_proxy_url()","metadata":{"trusted":true},"outputs":[],"execution_count":null},{"cell_type":"code","source":"!adk web --url_prefix {url_prefix}","metadata":{"trusted":true},"outputs":[],"execution_count":null}]}